<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of service * * @author Suharshana DsW */class Service {    public $id;    public $title;    public $image_name;    public $short_description;    public $description;    public $queue;    public function __construct($id) {        if ($id) {            $db = new Database();            $id = mysql_real_escape_string($id);            $query = "SELECT `id`,`title`,`image_name`,`short_description`,`description`,`queue` FROM `service` WHERE `id`=" . $id;            $result = mysql_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->title = $result['title'];            $this->image_name = $result['image_name'];            $this->short_description = $result['short_description'];            $this->description = $result['description'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $db = new Database();        $title = mysql_real_escape_string($this->title);        $short_description = mysql_real_escape_string($this->short_description);        $description = mysql_real_escape_string($this->description);        $query = "INSERT INTO `service` (`title`,`image_name`,`short_description`,`description`,`queue`) VALUES  ('"                . $title . "','"                . $this->image_name . "', '"                . $short_description . "', '"                . $description . "', '"                . $this->queue . "')";        $result = $db->readQuery($query);        if ($result) {            $last_id = mysql_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `service` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $db = new Database();        $title = mysql_real_escape_string($this->title);        $short_description = mysql_real_escape_string($this->short_description);        $description = mysql_real_escape_string($this->description);        $query = "UPDATE  `service` SET "                . "`title` ='" . $title . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`short_description` ='" . $short_description . "', "                . "`description` ='" . $description . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $this->deletePhotos();        unlink(Helper::getSitePath() . "upload/service/" . $this->image_name);        $query = 'DELETE FROM `service` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deletePhotos() {        $SERVICE_PHOTO = new ServicePhoto(NULL);        $allPhotos = $SERVICE_PHOTO->getServicePhotosById($this->id);        foreach ($allPhotos as $photo) {            $IMG = $SERVICE_PHOTO->image_name = $photo["image_name"];            unlink(Helper::getSitePath() . "upload/service/gallery/" . $IMG);            unlink(Helper::getSitePath() . "upload/service/gallery/thumb/" . $IMG);            $SERVICE_PHOTO->id = $photo["id"];            $SERVICE_PHOTO->delete();        }    }    public function arrange($key, $img) {        $query = "UPDATE `service` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}